import echarts from 'echarts';
export default {
  data: function data() {
    return {
      aaa: '',
      chart: null,
      className: {
        type: String,
        default: 'chart'
      }
    };
  },
  mounted: function mounted() {
    this.initChart();
  },
  beforeDestroy: function beforeDestroy() {
    if (!this.chart) {
      return;
    }

    this.chart.dispose();
    this.chart = null;
  },
  methods: {
    initChart: function initChart() {
      this.chart = echarts.init(document.getElementById('charts'));
      this.chart.setOption({
        toolbox: {
          show: true,
          // 图例，切换视图
          feature: {
            mark: {
              show: true
            },
            dataView: {
              show: true,
              readOnly: false
            },
            magicType: {
              show: true,
              type: ['line', 'bar']
            },
            restore: {
              show: true
            },
            saveAsImage: {
              show: true
            }
          }
        },
        title: {
          top: 20,
          text: '登录日志报表',
          textStyle: {
            fontWeight: 'normal',
            fontSize: 16
          },
          left: '1%'
        },
        tooltip: {
          trigger: 'axis',
          // 悬浮竖线
          axisPointer: {
            lineStyle: {
              color: 'rgb(0, 135, 211)',
              width: 2
            }
          }
        },
        // 上部图例  
        legend: {
          top: 20,
