import echarts from 'echarts'
export default {
  data (){
    return {
      aaa: '',
      chart: null,
      className: {
        type: String,
        default: 'chart'
      }
    }
  },
  mounted() {
    this.initChart()
  },
  beforeDestroy() {
    if (!this.chart) {
      return
    }
    this.chart.dispose()
    this.chart = null
  },
  methods: {
    initChart() {
      this.chart = echarts.init(document.getElementById('charts'))
      this.chart.setOption({
        toolbox: {
          show : true,
            // 图例，切换视图
          feature : {
            mark : {show: true},
            dataView : {show: true, readOnly: false},
            magicType : {show: true, type: ['line', 'bar']},
            restore : {show: true},
            saveAsImage : {show: true}
          }
        },
        title: {
          top: 20,
          text: '登录日志报表',
          textStyle: {
            fontWeight: 'normal',
            fontSize: 16,
          },
          left: '1%'
        },
        tooltip: {
          trigger: 'axis',
          // 悬浮竖线
          axisPointer: {
            lineStyle: {
              color: 'rgb(0, 135, 211)',
              width: 2
            }
          }
        },
        // 上部图例  
        legend: {
          top: 20,
          // icon: 'rect',
          // itemWidth: 20,
          // itemHeight: 5,
          // itemGap: 13,
          data: ['登录次数'],
          right: '50%',
          textStyle: {
            fontSize: 14,
          }
        },
        // 边距控制
        grid: {
          top: 100,
          left: '2%',
          right: '2%',
          bottom: '2%',
          containLabel: true
        },
        //X轴
        xAxis: [{
          type: 'category',
          //覆盖整个区域
          boundaryGap: false,
          data: ['07-01', '07-03', '07-03', '07-03', '07-03', '07-03', '07-03', '07-03', '07-03', '07-03', '07-03', '07-03']
        }],
        // Y轴
        yAxis: [{
          type: 'value',
          axisTick: {
            show: false
          },
          // axisLine: {
          //   lineStyle: {
          //     color: '#57617B',
          //   }
          // },
          axisLabel: {
            margin: 10,
            textStyle: {
              fontSize: 14
            }
          },
          // 背景线
          // splitLine: {
          //   lineStyle: {
          //     color: '#blue'
          //   }
          // }
        }],
        // 栏目
        series: [{
          name: '登录次数',
          type: 'line',
          // smooth: false,
          // symbol: 'circle',
          symbolSize: 6,
          // showSymbol: true,
          lineStyle: {
            normal: {
              width: 2,
            }
          },
          // 折现图区域样式
          areaStyle: {
            normal: {
              color:'rgb(242,100,98)',
            }
          },
          // 折线颜色与柱状图颜色
          itemStyle: {
            normal: {
              color:'rgb(235, 0, 0)',
              areaStyle: {type: 'default'},
            },
            //鼠标悬浮样式
            emphasis:{
              lineStyle:{
                color:'blue',
                type: 'dashed',
                width:5
              }
            }
          },
          data: [220, 182, 191, 134, 150, 120, 110, 125, 145, 122, 165, 122]
        }]
      })
